KJE.BuyvsLeaseCalc=function(){this.MSG_ERROR1=KJE.parameters.get("MSG_ERROR1","Your loan term must be equal to or greater than the lease term.");
this.MSG_ERROR2=KJE.parameters.get("MSG_ERROR2","Your down payment must be less than or equal to the total purchase price.");
this.DS_NET=KJE.FloatArray(2);
this.cats=KJE.parameters.get("ARRAY_GRAPH_CATS",["Net Cost to Buy","Net Cost to Lease"]);
this.sSchedule=new KJE.Repeating()
};
KJE.BuyvsLeaseCalc.prototype.clear=function(){this.TERM=0;
this.SECURITY_DEPOSIT=0;
this.OTHER_FEES_LEASE=0;
this.CAR_PRICE=0;
this.INTEREST_RATE=0;
this.DOWN_PAYMENT=0;
this.OTHER_FEES_BUY=0;
this.SALES_TAX_RATE=0;
this.RATE_OF_DEPRECIATION=0;
this.INVESTMENT_ROR=0;
this.LOAN_TERM=0;
this.LEASE_INTEREST=0;
this.RESIDUAL_PERCENT=0
};
KJE.BuyvsLeaseCalc.prototype.calculate=function(W){var at=KJE;
var O=this.TERM;
var ap=this.SECURITY_DEPOSIT;
var ab=this.OTHER_FEES_LEASE;
var ac=this.CAR_PRICE;
var au=this.INTEREST_RATE;
var ae=this.DOWN_PAYMENT;
var ar=this.OTHER_FEES_BUY;
var av=this.SALES_TAX_RATE;
var aw=this.RATE_OF_DEPRECIATION;
var Y=this.INVESTMENT_ROR;
var P=this.LOAN_TERM;
var N=this.LEASE_INTEREST;
var al=this.RESIDUAL_PERCENT;
var V=O/12;
if(P==0){P=O
}if(O>P){throw (this.MSG_ERROR1)
}var n=ae+ab;
var ai=(ac*(al/100));
var Q=ai*(N/1200);
Q+=KJE.PMT(N/1200,O,ac-ae-ai);
Q*=(1+(av/100));
var ad=Q*O;
var af=n+ad;
var R=KJE.FV_AMT(Y/100,V,ap+n)-ap-n;
var X=R+af;
var ao=X/O;
var aa=ac*(av/100);
var U=ac+aa+ar;
if(ae>U){throw (this.MSG_ERROR2)
}var ak=U-ae;
var Z=ac;
for(var S=1;
S<=V;
S++){Z=Z*(1-aw/100)
}var an=KJE.PMT(au/1200,P,ak);
var aj=0;
var T=an*O;
var M=KJE.FV_AMT(Y/100,V,ae+ar)-ae-ar;
var ah=ak;
var aq=KJE.ROR_MONTH(Y/100);
for(var S=1;
S<=O;
S++){ah-=(an-((ah*au)/1200));
aj=(an-Q+aj)*(aq+1)
}aj-=(an-Q)*O;
M+=aj;
var am=T+ae+M-Z+ah;
var ag=am/O;
this.LOST_PAYMENT_INTEREST=aj;
this.TOTAL_UPFRONT_COSTS=n;
this.TOTAL_LEASE_PAYMENTS=ad;
this.TOTAL_PAID_FOR_LEASE=af;
this.LOST_INTEREST_ON_LEASE=R;
this.NET_COST_OF_LEASE=X;
this.AVERAGE_COST_PER_MONTH_LEASE=ao;
this.MARKET_VALUE_AT_END_OF_LOAN=Z;
this.SALES_TAX=aa;
this.TOTAL_PURCHASE_PRICE=U;
this.LOAN_AMOUNT=ak;
this.LOAN_PAYMENT=an;
this.TOTAL_PAYMENTS=T;
this.LOST_INTEREST_ON_BUY=M;
this.NET_COST_OF_PURCHASE=am;
this.AVERAGE_COST_PER_MONTH_BUY=ag;
this.ENDING_LOAN_BALANCE=ah;
this.MONTHLY_PAYMENT=Q;
this.RESIDUAL_AMT=ai;
this.DS_NET[0]=am;
this.DS_NET[1]=X
};
KJE.BuyvsLeaseCalc.prototype.formatReport=function(b){b.percent("LEASE_INTEREST",this.LEASE_INTEREST/100,2);
b.percent("RESIDUAL_PERCENT",this.RESIDUAL_PERCENT/100);
b.dollars("RESIDUAL_AMT",this.RESIDUAL_AMT);
b.dollars("ENDING_LOAN_BALANCE",this.ENDING_LOAN_BALANCE);
b.number("LOAN_TERM",this.LOAN_TERM);
b.dollars("LOST_INTEREST_ON_LEASE",this.LOST_INTEREST_ON_LEASE);
b.number("TERM",this.TERM);
b.dollars("MONTHLY_PAYMENT",this.MONTHLY_PAYMENT);
b.dollars("SECURITY_DEPOSIT",this.SECURITY_DEPOSIT);
b.dollars("OTHER_FEES_LEASE",this.OTHER_FEES_LEASE);
b.dollars("TOTAL_UPFRONT_COSTS",this.TOTAL_UPFRONT_COSTS);
b.dollars("TOTAL_LEASE_PAYMENTS",this.TOTAL_LEASE_PAYMENTS);
b.dollars("TOTAL_PAID_FOR_LEASE",this.TOTAL_PAID_FOR_LEASE);
b.dollars("NET_COST_OF_LEASE",this.NET_COST_OF_LEASE);
b.dollars("AVERAGE_COST_PER_MONTH_LEASE",this.AVERAGE_COST_PER_MONTH_LEASE);
b.dollars("CAR_PRICE",this.CAR_PRICE);
b.loanRate("INTEREST_RATE",this.INTEREST_RATE/100);
b.dollars("DOWN_PAYMENT",this.DOWN_PAYMENT);
b.dollars("OTHER_FEES_BUY",this.OTHER_FEES_BUY);
b.taxRate("SALES_TAX_RATE",this.SALES_TAX_RATE/100);
b.percent("RATE_OF_DEPRECIATION",this.RATE_OF_DEPRECIATION/100);
b.dollars("MARKET_VALUE_AT_END_OF_LOAN",this.MARKET_VALUE_AT_END_OF_LOAN);
b.dollars("SALES_TAX",this.SALES_TAX);
b.dollars("TOTAL_PURCHASE_PRICE",this.TOTAL_PURCHASE_PRICE);
b.dollars("LOAN_AMOUNT",this.LOAN_AMOUNT);
b.dollars("LOAN_PAYMENT",this.LOAN_PAYMENT);
b.dollars("TOTAL_PAYMENTS",this.TOTAL_PAYMENTS);
b.dollars("TOTAL_DOWN_PLUS_PAYMENTS",this.TOTAL_PAYMENTS);
b.dollars("TOTAL_DOWN_LOST_INTEREST_PLUS_PAYMENTS",this.TOTAL_PAYMENTS);
b.dollars("LOST_INTEREST_ON_BUY",this.LOST_INTEREST_ON_BUY);
b.dollars("NET_COST_OF_PURCHASE",this.NET_COST_OF_PURCHASE);
b.dollars("AVERAGE_COST_PER_MONTH_BUY",this.AVERAGE_COST_PER_MONTH_BUY);
b.returnRate("INVESTMENT_ROR",this.INVESTMENT_ROR/100);
b.dollars("LOST_PAYMENT_INTEREST",this.LOST_PAYMENT_INTEREST);
b.dollars("LEASE_TOT_DOWN_PLUS_PAYMENTS",this.TOTAL_LEASE_PAYMENTS);
b.dollars("LEASE_TOT_DOWN_LOST_INTEREST_PLUS_PAYMENTS",this.TOTAL_LEASE_PAYMENTS);
b.replace("**REPEATING GROUP**",this.sSchedule.getRepeat())
};
KJE.CalcName="Equipment Buy vs. Lease Calculator";
