KJE.Label("NET_COST_OF_PURCHASE","Net cost of buying",null,null,"KJEBold");
var s=KJE.gNewGraph(KJE.gCATEGORIES,"GRAPH1",true,false,KJE.colorList[1],KJE.parameters.get("MSG_GRAPH_TITLE","Net Cost of Buying vs. Leasing"));
