KJE.BusinessValuationCalc=function(){this.YEARS_OF_CASHFLOWS=1;
this.PROJECT_YEARS=100;
this.OPERATING_PROFIT=0;
this.INTEREST_EXPENSE=0;
this.INTEREST_INCOME=0;
this.INCOME_TAXES=0;
this.DEPRECIATION_AND_AMORTIZATION=0;
this.CASH_FLOW_FROM_OPERATIONS=0;
this.CHANGE_IN_NOTES_PAYABLE=0;
this.CHANGE_IN_INVENTORY=0;
this.CHANGE_IN_ACCOUNTS_RECEIVABLE=0;
this.CHANGES_IN_OPERATING_ASSETS_LIABILITIES=0;
this.OTHER_NET_CHANGE=0;
this.NET_CASH_FLOW_FOR_OPERATIONS=0;
this.CAPITAL_EXPENDITURES=0;
this.OTHER_NET_AMOUNT=0;
this.CASH_FLOW_FROM_INVESTMENT_ACTVITIES=0;
this.NET_CASH_FLOW=0;
this.MSG_XAXIS_TITLE=KJE.parameters.get("MSG_XAXIS_TITLE","A higher growth rate can dramatically increase value.");
this.MSG_GRAPH_TITLE=KJE.parameters.get("MSG_GRAPH_TITLE","How Growth Affects Business Valuation");
this.MSG_ERROR1=KJE.parameters.get("MSG_ERROR1","Net cash flow can't be negative for this calculation.");
this.GROWTH_AMT=KJE.FloatArray(3);
this.cats=new Array(3);
this.sSchedule=new KJE.Repeating()
};
KJE.BusinessValuationCalc.prototype.clear=function(){this.YEAR_TO_START=0;
this.EXPECTED_ANNUAL_GROWTH=0;
this.WEIGHTED_AVERAGE_COST_OF_CAPITAL=0
};
KJE.BusinessValuationCalc.prototype.calculate=function(k){var p=KJE;
var s=this.YEAR_TO_START;
var l=this.EXPECTED_ANNUAL_GROWTH/100;
var r=this.WEIGHTED_AVERAGE_COST_OF_CAPITAL/100;
var m=this.PROJECT_YEARS;
this.CASH_FLOW_FROM_OPERATIONS=this.OPERATING_PROFIT+this.INTEREST_INCOME+ +this.DEPRECIATION_AND_AMORTIZATION-this.INCOME_TAXES-this.INTEREST_EXPENSE;
this.CHANGES_IN_OPERATING_ASSETS_LIABILITIES=this.CHANGE_IN_NOTES_PAYABLE+this.OTHER_NET_CHANGE-this.CHANGE_IN_INVENTORY-this.CHANGE_IN_ACCOUNTS_RECEIVABLE;
this.NET_CASH_FLOW_FOR_OPERATIONS=this.CASH_FLOW_FROM_OPERATIONS+this.CHANGES_IN_OPERATING_ASSETS_LIABILITIES;
this.CASH_FLOW_FROM_INVESTMENT_ACTVITIES=this.OTHER_NET_AMOUNT-this.CAPITAL_EXPENDITURES;
this.NET_CASH_FLOW=this.NET_CASH_FLOW_FOR_OPERATIONS+this.CASH_FLOW_FROM_INVESTMENT_ACTVITIES;
if(this.NET_CASH_FLOW<0){throw this.MSG_ERROR1
}this.NET_CASH_NPV1=KJE.NPV_AMT(r,1,this.NET_CASH_FLOW);
var o=this.NET_CASH_NPV1;
this.NET_CASH_FLOW2=(l+1)*this.NET_CASH_FLOW;
this.NET_CASH_FLOW3=(l+1)*this.NET_CASH_FLOW2;
this.NET_CASH_FLOW4=(l+1)*this.NET_CASH_FLOW3;
this.NET_CASH_NPV2=KJE.NPV_AMT(r,2,this.NET_CASH_FLOW2);
this.NET_CASH_NPV3=KJE.NPV_AMT(r,3,this.NET_CASH_FLOW3);
this.NET_CASH_NPV4=KJE.NPV_AMT(r,4,this.NET_CASH_FLOW4);
this.GROWTH_AMT[0]=o;
this.GROWTH_AMT[1]=o;
this.GROWTH_AMT[2]=o;
var n=this.NET_CASH_FLOW;
if(n>0){for(var q=this.YEARS_OF_CASHFLOWS;
q<m;
q++){n*=(1+l);
this.GROWTH_AMT[0]+=KJE.NPV_AMT(r,q+1,n)
}n=this.NET_CASH_FLOW;
for(var q=this.YEARS_OF_CASHFLOWS;
q<m;
q++){n*=(1+(l+0.01));
this.GROWTH_AMT[1]+=KJE.NPV_AMT(r,q+1,n)
}n=this.NET_CASH_FLOW;
for(var q=this.YEARS_OF_CASHFLOWS;
q<m;
q++){n*=(1+(l+0.02));
this.GROWTH_AMT[2]+=KJE.NPV_AMT(r,q+1,n)
}}o=this.GROWTH_AMT[0];
this.cats[0]=p.percent((l),1);
this.cats[1]=p.percent((l+0.01),1);
this.cats[2]=p.percent((l+0.02),1);
this.NPV=o
};
KJE.BusinessValuationCalc.prototype.formatReport=function(d){d.year("PROJECT_YEARS",this.PROJECT_YEARS);
d.year("PROJECT_ADDITIONAL_YEARS",this.PROJECT_YEARS-1);
d.dollars("OPERATING_PROFIT",this.OPERATING_PROFIT);
d.dollars("INTEREST_EXPENSE",this.INTEREST_EXPENSE);
d.dollars("INTEREST_INCOME",this.INTEREST_INCOME);
d.dollars("INCOME_TAXES",this.INCOME_TAXES);
d.dollars("DEPRECIATION_AND_AMORTIZATION",this.DEPRECIATION_AND_AMORTIZATION);
d.dollars("CASH_FLOW_FROM_OPERATIONS",this.CASH_FLOW_FROM_OPERATIONS);
d.dollars("CHANGE_IN_NOTES_PAYABLE",this.CHANGE_IN_NOTES_PAYABLE);
d.dollars("CHANGE_IN_INVENTORY",this.CHANGE_IN_INVENTORY);
d.dollars("CHANGE_IN_ACCOUNTS_RECEIVABLE",this.CHANGE_IN_ACCOUNTS_RECEIVABLE);
d.dollars("CHANGES_IN_OPERATING_ASSETS_LIABILITIES",this.CHANGES_IN_OPERATING_ASSETS_LIABILITIES);
d.dollars("OTHER_NET_CHANGE",this.OTHER_NET_CHANGE);
d.dollars("NET_CASH_FLOW_FOR_OPERATIONS",this.NET_CASH_FLOW_FOR_OPERATIONS);
d.dollars("CAPITAL_EXPENDITURES",this.CAPITAL_EXPENDITURES);
d.dollars("OTHER_NET_AMOUNT",this.OTHER_NET_AMOUNT);
d.dollars("CASH_FLOW_FROM_INVESTMENT_ACTVITIES",this.CASH_FLOW_FROM_INVESTMENT_ACTVITIES);
d.dollars("NET_CASH_FLOW2",this.NET_CASH_FLOW2);
d.dollars("NET_CASH_FLOW3",this.NET_CASH_FLOW3);
d.dollars("NET_CASH_FLOW4",this.NET_CASH_FLOW4);
d.dollars("NET_CASH_FLOW",this.NET_CASH_FLOW);
d.dollars("NET_CASH_NPV1",this.NET_CASH_NPV1);
d.dollars("NET_CASH_NPV2",this.NET_CASH_NPV2);
d.dollars("NET_CASH_NPV3",this.NET_CASH_NPV3);
d.dollars("NET_CASH_NPV4",this.NET_CASH_NPV4);
d.percent("EXPECTED_ANNUAL_GROWTH",this.EXPECTED_ANNUAL_GROWTH/100,2);
d.percent("WEIGHTED_AVERAGE_COST_OF_CAPITAL",this.WEIGHTED_AVERAGE_COST_OF_CAPITAL/100,2);
d.dollars("NPV_VALUE",this.NPV);
i=0;
for(var c=0;
c<3;
c++){i=c+1;
d.percent("GR_RATE"+i,(this.EXPECTED_ANNUAL_GROWTH+c)/100);
d.dollars("VALUE_RATE"+i,this.GROWTH_AMT[c])
}};
