KJE.CashFlowCalc=function(){this.DS_CASHFLOW=KJE.FloatArray(2);
this.cats=new Array(2);
this.cats[0]=KJE.parameters.get("MSG_GRAPH_COL1","Beginning");
this.cats[1]=KJE.parameters.get("MSG_GRAPH_COL2","Ending");
this.sSchedule=new KJE.Repeating()
};
