KJE.EmergencyCalc=function(){this.SHOW_TAXES=true;
this.NBR_PERIODS=12;
this.MONTHS_BF_TARGET=0;
this.sCats=null;
this.DS_NEW=null;
this.sSchedule=new KJE.Repeating()
};
KJE.EmergencyCalc.prototype.clear=function(){this.EMERGENCY_MEDICAL=0;
this.EMERGENCY_LEGAL=0;
this.EMERGENCY_AUTO_REPAIR=0;
this.EMERGENCY_PROPERTY_DAMAGE=0;
this.EMERGENCY_OTHER=0;
this.INSURANCE_DEDUCTIBLE_MEDICAL=0;
this.INSURANCE_DEDUCTIBLE_AUTO=0;
this.INSURANCE_DEDUCTIBLE_PROPERTY=0;
this.INSURANCE_DEDUCTIBLE_OTHER=0;
this.MONTHLY_LIVING_EXPENSES=0;
this.MONTHS_OF_UNEMPLOYMENT=0;
this.AMT_CURRENT=0;
this.AMT_SAVE_MONTH=0;
this.ROR_INVEST=0;
this.FED_TAX_RATE=0;
this.STATE_TAX_RATE=0;
this.ROR_AFTER_TAXES=0;
this.MARGINAL_TAX_RATE=0
};
KJE.EmergencyCalc.prototype.calculate=function(M){var aa=KJE;
var af=this.EMERGENCY_MEDICAL;
var R=this.EMERGENCY_LEGAL;
var i=this.EMERGENCY_AUTO_REPAIR;
var N=this.EMERGENCY_PROPERTY_DAMAGE;
var U=this.EMERGENCY_OTHER;
var S=this.INSURANCE_DEDUCTIBLE_MEDICAL;
var G=this.INSURANCE_DEDUCTIBLE_AUTO;
var O=this.INSURANCE_DEDUCTIBLE_PROPERTY;
var K=this.INSURANCE_DEDUCTIBLE_OTHER;
var ad=this.MONTHLY_LIVING_EXPENSES;
var P=this.MONTHS_OF_UNEMPLOYMENT;
var L=this.AMT_CURRENT;
var V=this.AMT_SAVE_MONTH;
var I=this.ROR_INVEST;
var J=this.FED_TAX_RATE;
var ae=this.STATE_TAX_RATE;
var E=this.MARGINAL_TAX_RATE;
var ac=af+R+i+N+U;
var X=S+G+O+K;
var Q=ad*P;
var W=Q+X+ac;
if(E==0){E=ae+J
}var Y=(I/100)*(1-E/100);
this.ROR_MONTHLY_PERC=KJE.ROR_MONTH((this.SHOW_TAXES?Y:I/100));
var ab=Math.ceil(this.solveForMonths(W,L,this.ROR_MONTHLY_PERC,V));
this.MSG_MONTHS_TO_TARGET=KJE.getTermLabel(ab);
var F=Math.floor(Math.round(ab)/this.NBR_PERIODS);
ab=(ab-F*12);
var T=F+2;
this.DS_NEW=KJE.FloatArray(T);
this.sCats=KJE.FloatArray(T);
if(M){var Z=this.sSchedule;
Z.clearRepeat();
Z.addHeader(Z.sReportCol("Year",1),Z.sReportCol("Contributions",2),Z.sReportCol("Interest Earned",3),Z.sReportCol("Balance",4));
Z.addRepeat("&nbsp;","&nbsp;","&nbsp;",aa.dollars(L)+" "+Z.sReportCol("Starting Balance",5)+" ")
}for(var H=0;
H<T;
H++){this.sCats[H]=""+H;
this.DS_NEW[H]=(this.solveAmtAtYear(H,V));
if(M&&H>0){Z.addRepeat(H,aa.dollars(V*12),aa.dollars(this.solveAmtAtYear(H,V)-this.solveAmtAtYear(H-1,V)-V*12),aa.dollars(this.solveAmtAtYear(H,V)))
}}this.ROR_AFTER_TAXES=Y;
this.AMT_TARGET=W;
this.MONTHS_TO_TARGET=ab;
this.YEARS_TO_TARGET=F;
this.EMERGENCY_TOTAL=ac;
this.INSURANCE_DEDUCTIBLE_TOTAL=X;
this.LIVING_EXPENSE_TOTAL=Q
};
KJE.EmergencyCalc.prototype.formatReport=function(b){b.dollars("AMT_CURRENT",this.AMT_CURRENT);
b.dollars("AMT_SAVE_MONTH",this.AMT_SAVE_MONTH);
b.returnRate("ROR_INVEST",this.ROR_INVEST/100);
b.taxRate("FED_TAX_RATE",this.FED_TAX_RATE/100);
b.taxRate("STATE_TAX_RATE",this.STATE_TAX_RATE/100);
b.taxRate("MARGINAL_TAX_RATE",(this.STATE_TAX_RATE+this.FED_TAX_RATE)/100);
b.returnRate("ROR_MONTHLY_PERC",this.ROR_MONTHLY_PERC);
b.replace("MSG_MONTHS_TO_TARGET",this.MSG_MONTHS_TO_TARGET);
b.number("MONTHS_TO_TARGET",this.MONTHS_TO_TARGET);
b.dollars("AMT_TARGET",this.AMT_TARGET);
b.number("NBR_PERIODS",this.NBR_PERIODS);
b.dollars("EMERGENCY_MEDICAL",this.EMERGENCY_MEDICAL);
b.dollars("EMERGENCY_LEGAL",this.EMERGENCY_LEGAL);
b.dollars("EMERGENCY_AUTO_REPAIR",this.EMERGENCY_AUTO_REPAIR);
b.dollars("EMERGENCY_PROPERTY_DAMAGE",this.EMERGENCY_PROPERTY_DAMAGE);
b.dollars("EMERGENCY_OTHER",this.EMERGENCY_OTHER);
b.dollars("INSURANCE_DEDUCTIBLE_MEDICAL",this.INSURANCE_DEDUCTIBLE_MEDICAL);
b.dollars("INSURANCE_DEDUCTIBLE_AUTO",this.INSURANCE_DEDUCTIBLE_AUTO);
b.dollars("INSURANCE_DEDUCTIBLE_PROPERTY",this.INSURANCE_DEDUCTIBLE_PROPERTY);
b.dollars("INSURANCE_DEDUCTIBLE_OTHER",this.INSURANCE_DEDUCTIBLE_OTHER);
b.dollars("MONTHLY_LIVING_EXPENSES",this.MONTHLY_LIVING_EXPENSES);
b.number("MONTHS_OF_UNEMPLOYMENT",this.MONTHS_OF_UNEMPLOYMENT);
b.dollars("EMERGENCY_TOTAL",this.EMERGENCY_TOTAL);
b.dollars("INSURANCE_DEDUCTIBLE_TOTAL",this.INSURANCE_DEDUCTIBLE_TOTAL);
b.dollars("LIVING_EXPENSE_TOTAL",this.LIVING_EXPENSE_TOTAL);
b.taxRate("MARGINAL_TAX_RATE",this.MARGINAL_TAX_RATE/100);
b.returnRate("ROR_AFTER_TAXES",this.ROR_AFTER_TAXES);
b.replace("**REPEATING GROUP**",this.sSchedule.getRepeat())
};
KJE.EmergencyCalc.prototype.solveForMonths=function(i,h,j,l){var m=1000/2;
var k=1000/4;
for(var n=0;
n<30;
n++){if(this.ifTargetGreater(i,h,j,m,l)){m+=k
}else{m-=k
}k=k/2
}return m
};
KJE.EmergencyCalc.prototype.solveAmtAtYear=function(d,c){return(this.AMT_CURRENT/(Math.pow((1+this.ROR_MONTHLY_PERC),(-1*d*this.NBR_PERIODS))))-c+(c/this.ROR_MONTHLY_PERC)*((Math.pow((1+this.ROR_MONTHLY_PERC),d*this.NBR_PERIODS+1))-1)
};
KJE.EmergencyCalc.prototype.ifTargetGreater=function(g,f,h,j,i){return(g>(f/(Math.pow((1+h),(-1*j))))-i+(i/h)*((Math.pow((1+h),j+1))-1))
};
KJE.CalcName="Emergency Savings Calculator";
