KJE.DollarSlider("EMERGENCY_PROPERTY_DAMAGE","Emergency property damage",0,100000);
KJE.DollarSlider("EMERGENCY_OTHER","Emergency other",0,100000);
KJE.DollarSlider("INSURANCE_DEDUCTIBLE_MEDICAL","Insurance deductible medical",0,100000);
KJE.DollarSlider("INSURANCE_DEDUCTIBLE_AUTO","Insurance deductible auto",0,100000);
