KJE.NetWorthCalc=function(){this.YEARS_TO_PROJECT=KJE.parameters.get("YEARS_TO_PROJECT",10);
this.DS_WORTH=KJE.FloatArray(3);
this.DR_OVER_TIME=null;
this.DR_LIABILITIES=null;
this.DR_ASSETS=null;
this.cats=null;
this.sSchedule=new KJE.Repeating()
};
KJE.NetWorthCalc.prototype.clear=function(){this.HOME=0;
this.OTHER_REAL_ESTATE=0;
this.AUTOMOBILES=0;
this.OTHER_VEHICLES=0;
this.JEWELRY=0;
this.HOUSEHOLD_ITEMS=0;
this.CHECKING_AND_SAVINGS=0;
this.RETIREMENT_ACCOUNTS=0;
this.SAVINGS_BONDS=0;
this.BONDS=0;
this.MUTUAL_FUNDS=0;
this.STOCKS=0;
this.LIFE_INSURANCE=0;
this.CASH=0;
this.OTHER=0;
this.HOME_MORTGAGE_PRINCIPAL=0;
this.OTHER_MORTGAGE_PRINCIPAL=0;
this.AUTO_LOANS=0;
this.STUDENT_LOANS=0;
this.OTHER_LOANS=0;
this.CREDIT_CARD_DEBT=0;
this.LINES_OF_CREDIT=0;
this.DEBT_GROWTH=0;
this.ASSET_GROWTH=0
};
KJE.NetWorthCalc.prototype.calculate=function(O){var ac=KJE;
var ak=this.HOME;
var ai=this.OTHER_REAL_ESTATE;
var M=this.AUTOMOBILES;
var R=this.OTHER_VEHICLES;
var U=this.JEWELRY;
var aj=this.HOUSEHOLD_ITEMS;
var S=this.CHECKING_AND_SAVINGS;
var i=this.RETIREMENT_ACCOUNTS;
var Q=this.SAVINGS_BONDS;
var ap=this.BONDS;
var ab=this.MUTUAL_FUNDS;
var Y=this.STOCKS;
var ao=this.LIFE_INSURANCE;
var K=this.CASH;
var T=this.OTHER;
var ah=this.HOME_MORTGAGE_PRINCIPAL;
var N=this.OTHER_MORTGAGE_PRINCIPAL;
var af=this.AUTO_LOANS;
var W=this.STUDENT_LOANS;
var am=this.OTHER_LOANS;
var ad=this.CREDIT_CARD_DEBT;
var ae=this.LINES_OF_CREDIT;
var ag=this.DEBT_GROWTH;
var an=this.ASSET_GROWTH;
var P=this.YEARS_TO_PROJECT;
var X=ak+ai+M+R+U+aj+S+i+Q+ap+ab+Y+ao+K+T;
var al=ah+N+af+W+am+ad+ae;
var n=X-al;
this.DS_WORTH[0]=(X);
this.DS_WORTH[1]=(al);
this.DS_WORTH[2]=(n);
var V=Math.round(P);
var aa=0;
this.dMax=X-al;
this.dMin=X-al;
this.DR_OVER_TIME=KJE.FloatArray(V);
this.DR_ASSETS=KJE.FloatArray(V);
this.DR_LIABILITIES=KJE.FloatArray(V);
for(var L=0;
L<V;
L++){this.DR_ASSETS[L]=KJE.FV_AMT(an/100,L,X);
this.DR_LIABILITIES[L]=KJE.FV_AMT(ag/100,L,al);
this.DR_OVER_TIME[L]=this.DR_ASSETS[L]-this.DR_LIABILITIES[L];
if(this.DR_OVER_TIME[L]>this.dMax){this.dMax=this.DR_OVER_TIME[L]
}if(this.DR_OVER_TIME[L]<this.dMin){this.dMin=this.DR_OVER_TIME[L]
}}this.cats=KJE.FloatArray(V);
if(O){var Z=this.sSchedule;
Z.clearRepeat();
Z.addHeader(Z.sReportCol(" Year ",1),Z.sReportCol("Assets",2),Z.sReportCol("Liabilities",3),Z.sReportCol("Net Worth",4))
}for(var L=1;
L<=V;
L++){aa=L-1;
this.cats[aa]=""+L;
if(O){Z.addRepeat(+L,ac.dollars(this.DR_ASSETS[aa]),ac.dollars(this.DR_LIABILITIES[aa]),ac.dollars(this.DR_OVER_TIME[aa]))
}}this.TOTAL_ASSETS=X;
this.TOTAL_LIABILITIES=al;
this.NET_WORTH=n
};
KJE.NetWorthCalc.prototype.formatReport=function(b){b.dollars("HOME_MORTGAGE_PRINCIPAL",this.HOME_MORTGAGE_PRINCIPAL);
b.dollars("OTHER_MORTGAGE_PRINCIPAL",this.OTHER_MORTGAGE_PRINCIPAL);
b.dollars("HOME",this.HOME);
b.dollars("OTHER_REAL_ESTATE",this.OTHER_REAL_ESTATE);
b.dollars("AUTOMOBILES",this.AUTOMOBILES);
b.dollars("OTHER_VEHICLES",this.OTHER_VEHICLES);
b.dollars("JEWELRY",this.JEWELRY);
b.dollars("HOUSEHOLD_ITEMS",this.HOUSEHOLD_ITEMS);
b.dollars("CHECKING_AND_SAVINGS",this.CHECKING_AND_SAVINGS);
b.dollars("RETIREMENT_ACCOUNTS",this.RETIREMENT_ACCOUNTS);
b.dollars("SAVINGS_BONDS",this.SAVINGS_BONDS);
b.dollars("BONDS",this.BONDS);
b.dollars("MUTUAL_FUNDS",this.MUTUAL_FUNDS);
b.dollars("STOCKS",this.STOCKS);
b.dollars("LIFE_INSURANCE",this.LIFE_INSURANCE);
b.dollars("CASH",this.CASH);
b.dollars("AUTO_LOANS",this.AUTO_LOANS);
b.dollars("STUDENT_LOANS",this.STUDENT_LOANS);
b.dollars("OTHER_LOANS",this.OTHER_LOANS);
b.dollars("CREDIT_CARD_DEBT",this.CREDIT_CARD_DEBT);
b.dollars("LINES_OF_CREDIT",this.LINES_OF_CREDIT);
b.percent("DEBT_GROWTH",this.DEBT_GROWTH/100);
b.percent("ASSET_GROWTH",this.ASSET_GROWTH/100);
b.year("YEARS_TO_PROJECT",this.YEARS_TO_PROJECT);
b.dollars("TOTAL_ASSETS",this.TOTAL_ASSETS);
b.dollars("TOTAL_LIABILITIES",this.TOTAL_LIABILITIES);
b.dollars("FINAL_NET_WORTH",this.DR_OVER_TIME[this.YEARS_TO_PROJECT-1]);
b.dollars("NET_WORTH",this.NET_WORTH);
b.dollars("OTHER",this.OTHER);
b.replace("**REPEATING GROUP**",this.sSchedule.getRepeat())
};
KJE.CalcName="Net Worth Calculator";
KJE.CalcType="networth";
KJE.CalculatorTitleTemplate="Your current net worth is KJE1";
KJE.parseInputs=function(b){b=KJE.replace("**MSG_LABEL1**",KJE.parameters.get("MSG_LABEL1","Real Estate:"),b);
b=KJE.replace("**MSG_LABEL2**",KJE.parameters.get("MSG_LABEL2","Personal Property:"),b);
b=KJE.replace("**MSG_LABEL3**",KJE.parameters.get("MSG_LABEL3","Investments:"),b);
b=KJE.replace("**MSG_LABEL4**",KJE.parameters.get("MSG_LABEL4","Cash:"),b);
b=KJE.replace("**MSG_LABEL5**",KJE.parameters.get("MSG_LABEL5","Mortgage Principal:"),b);
b=KJE.replace("**MSG_LABEL6**",KJE.parameters.get("MSG_LABEL6","Loans and Debt:"),b);
return b
};
