KJE.RothvsRegularCalc.prototype.getRothMaximum=function(j,m,l){var n=this.dMaximumContribution(0,j);
var p=0;
m=(m?1:0);
if(l<this.ROTH_PHASEOUT[m][0]){p=n
}else{if(l>=this.ROTH_PHASEOUT[m][1]){p=0
}else{var i=l-this.ROTH_PHASEOUT[m][0];
var k=this.ROTH_PHASEOUT[m][1]-this.ROTH_PHASEOUT[m][0];
var o=KJE.round(i/k,3);
if(o>1){o=1
}p=n-(o*n);
if(p>=0&&p<200){p=200
}else{if(KJE.round(p,2)%10!=0){p+=10-(KJE.round(p,2)%10)
}}}}return p
};
KJE.RothvsRegularCalc.prototype.dMaximumContribution=function(f,e){var d=(f>=this.ANNUAL_MAX.length?this.ANNUAL_MAX.length-1:f);
return this.ANNUAL_MAX[d]+((e+f)<50?0:this.CATCHUP_MAX[d])
};
KJE.RothvsRegularCalc.prototype.dActualContribution=function(g,e,h,f){return(f&&g!=0?e:h)
};
KJE.RothvsRegularCalc.prototype.dMaximumDeduction=function(j,n,h,l,i){var k=0;
var m=(j>=this.ANNUAL_MAX.length?this.ANNUAL_MAX.length-1:j);
if(!h){k=i
}else{if(l){k=(1-((n-this.MARRIED_BREAKPOINTS[m][0])/(this.MARRIED_BREAKPOINTS[m][1]-this.MARRIED_BREAKPOINTS[m][0])))*i
}else{k=(1-((n-this.SINGLE_BREAKPOINTS[m][0])/(this.SINGLE_BREAKPOINTS[m][1]-this.SINGLE_BREAKPOINTS[m][0])))*i
}}if(k>=i){k=i
}else{if(k<=0){k=0
}else{if(k>0&&k<200){k=200
}else{if(KJE.round(k,2)%10!=0){k+=10-(KJE.round(k,2)%10)
}}}}return KJE.round(k,0)
};
KJE.RothvsRegularCalc.prototype.dActualDeduction=function(c,d){return(c>d?d:c)
};
